import os

import asyncio
from random import *
from gtts import gTTS
from pyrogram import Client, filters

import config

print('Bot started')

app = Client(
    'anon',
    config.api_id,
    config.api_hash
)


@app.on_message(filters.me & filters.command(commands=['команди', 'commands'], prefixes=['.', '!', '/']))
async def commands(client, message):
    await message.reply_text(
        '''
        Команди юб:
        .аудіо
        .мегапінг
        .спам
        .дел
        .скрол
        .пінг
        .краш
         .інфа
         ''')


@app.on_message(filters.me & filters.command(commands=['аудіо', 'audio'], prefixes=['.', '!', '/']))
async def audio(client, message):
    await app.delete_messages(message.chat.id, message.id)

    if message.reply_to_message:
        msg_text = message.reply_to_message.text
    else:
        msg_text = message.text[7:]

    obj = gTTS(text=msg_text, lang='ru', slow=False)
    name = randint(1, 1000)
    obj.save(f'{name}.mp3')

    await app.send_voice(message.chat.id, f"{name}.mp3")

    path = os.path.join(os.path.abspath(os.path.dirname(__file__)), f'{name}.mp3')
    os.remove(path)


@app.on_message(filters.me & filters.command(commands=['мегапінг', 'megaping'], prefixes=['.', '!', '/']))
async def all_ping(client, message):
    await message.delete()
    chat_id = message.chat.id

    members = app.get_chat_members(chat_id)
    strings = [[]]
    current_string = strings[0]
    max_length = 4000

    async for member in members:
        tag = member.user.username
        mention = member.user.mention if tag is None else f"@{tag}"

        if len(' '.join(current_string)) + len(mention) <= max_length:
            current_string.append(mention)
        else:
            current_string = [mention]
            strings.append(current_string)

    string = [' '.join(chunk) for chunk in strings]

    for stri in string:
        await app.send_message(message.chat.id, stri)


@app.on_message(filters.me & filters.command(commands=['spam', 'спам'], prefixes=['.', '!', '/']))
async def spam_command(client, message):
    msg = message.text.split(maxsplit=2)
    for i in range(int(msg[1])):
        await app.send_message(message.chat.id, msg[2])


@app.on_message(filters.me & filters.command(commands=['дел', 'del'], prefixes=['.', '!', '/']))
async def delete_all_messages_in_chat(client, message):
    await message.delete()

    async for item in app.search_messages(chat_id=message.chat.id, from_user=message.from_user.id):
        await app.delete_messages(message.chat.id, item.id)


@app.on_message(filters.me & filters.command(commands=['scroll', 'скрол'], prefixes=['.', '!', '/']))
async def scroll_text_command(client, message):
    text = message.text.split(maxsplit=1)[1]

    width = 10
    spaces = "ㅤ" * width
    text = spaces + text + spaces

    for i in range(len(text) - width + 1):
        await app.edit_message_text(message.chat.id, message.id, text[i:i + width])
        await asyncio.sleep(0.1)

    await message.delete()


@app.on_message(filters.me & filters.command(commands=['пінг', 'ping'], prefixes=['.', '!', '/']))
async def ping(client, message):
    await message.delete()

    user_id = message.reply_to_message.from_user.id

    await app.send_message(message.chat.id, f'<a href=tg://user?id={user_id}>{message.text[6:]}</a>')


