@app.on_message(filters.me & filters.command(commands=['інфа', 'info'], prefixes=['.', '!', '/']))
async def info(client, message):
    await message.delete()
    if message.reply_to_message:
        await app.send_message(message.from_user.id,
                               f"Інформація про повідомлення {message.reply_to_message.user.mention} у чаті @{message.reply_to_message.chat.username}:\n {message.reply_to_message}")

    else:
        await app.send_message(message.from_user.id,
                               f"Інформація про чат <a href=tg://user?id=1469275794>цеї людини</a> у чаті @{message.reply_to_message.chat.username}:\n {message.reply_to_message}")


